import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BookingList = () => {
    const [bookings, setBookings] = useState([]);
    const [editBooking, setEditBooking] = useState(null);

    // Fetch bookings
    useEffect(() => {
        axios.get('http://localhost:8080/api/bookings')
            .then(response => setBookings(response.data))
            .catch(error => console.error(error));
    }, []);

    // Delete booking
    const deleteBooking = (id) => {
        axios.delete(`http://localhost:8080/api/bookings/${id}`)
            .then(response => {
                setBookings(bookings.filter(booking => booking.id !== id));
            })
            .catch(error => console.error(error));
    };

    // Edit booking - set the form to existing booking data
    const handleEdit = (booking) => {
        setEditBooking(booking);
    };

    return React.createElement('div', { className: 'p-4' },
        React.createElement('h1', { className: 'text-2xl font-bold mb-4' }, 'Booking List'),
        React.createElement('table', { className: 'table-auto w-full border-collapse border border-gray-300' },
            React.createElement('thead', null,
                React.createElement('tr', null,
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'ID'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'Applicant Name'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'Email'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'Mobile'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'Start Date'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'End Date'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'Rent'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'Hall Name'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'Remarks'),
                    React.createElement('th', { className: 'border border-gray-300 px-4 py-2' }, 'Actions')
                )
            ),
            React.createElement('tbody', null,
                bookings.map(booking =>
                    React.createElement('tr', { key: booking.id },
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.id),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.applicantName),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.email),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.mobile),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.startDate),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.endDate),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.rent),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.hallName),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' }, booking.remarks),
                        React.createElement('td', { className: 'border border-gray-300 px-4 py-2' },
                            React.createElement('button', { onClick: () => handleEdit(booking), className: 'bg-yellow-500 text-white px-4 py-2 mr-2' }, 'Edit'),
                            React.createElement('button', { onClick: () => deleteBooking(booking.id), className: 'bg-red-500 text-white px-4 py-2' }, 'Delete')
                        )
                    )
                )
            )
        )
    );
};

export default BookingList;
