import React, { useState } from 'react';
import axios from 'axios';

const AddBooking = () => {
    const [formData, setFormData] = useState({
        applicantName: '',
        email: '',
        mobile: '',
        startDate: '',
        endDate: '',
        rent: '',
        additionalCharges: '',
        hallName: '',
        remarks: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost:8080/api/bookings', formData)
            .then(response => {
                alert('Booking added successfully!');
                setFormData({
                    applicantName: '',
                    email: '',
                    mobile: '',
                    startDate: '',
                    endDate: '',
                    rent: '',
                    additionalCharges: '',
                    hallName: '',
                    remarks: '',
                });
            })
            .catch(error => console.error(error));
    };

    return React.createElement('div', { className: 'p-4' },
        React.createElement('h1', { className: 'text-2xl font-bold mb-4' }, 'Add Booking'),
        React.createElement('form', { onSubmit: handleSubmit },
            React.createElement('input', { type: 'text', name: 'applicantName', placeholder: 'Applicant Name', value: formData.applicantName, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('input', { type: 'email', name: 'email', placeholder: 'Email', value: formData.email, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('input', { type: 'text', name: 'mobile', placeholder: 'Mobile', value: formData.mobile, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('input', { type: 'date', name: 'startDate', value: formData.startDate, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('input', { type: 'date', name: 'endDate', value: formData.endDate, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('input', { type: 'number', name: 'rent', placeholder: 'Rent', value: formData.rent, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('input', { type: 'number', name: 'additionalCharges', placeholder: 'Additional Charges', value: formData.additionalCharges, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('input', { type: 'text', name: 'hallName', placeholder: 'Hall Name', value: formData.hallName, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('textarea', { name: 'remarks', placeholder: 'Remarks', value: formData.remarks, onChange: handleChange, className: 'border p-2 w-full mb-4' }),
            React.createElement('button', { type: 'submit', className: 'bg-blue-500 text-white px-4 py-2' }, 'Add Booking')
        )
    );
};

export default AddBooking;
