import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookingForm = ({ selectedBooking, refreshBookings }) => {
  const [form, setForm] = useState({ name: '', date: '', hall: '' });

  useEffect(() => {
    if (selectedBooking) {
      setForm(selectedBooking);
    }
  }, [selectedBooking]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (form.id) {
      await axios.put(`http://localhost:8080/api/bookings/${form.id}`, form);
    } else {
      await axios.post('http://localhost:8080/api/bookings', form);
    }
    setForm({ name: '', date: '', hall: '' });
    refreshBookings();
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        value={form.name}
        onChange={handleChange}
        placeholder="Name"
        required
      />
      <input
        type="date"
        name="date"
        value={form.date}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="hall"
        value={form.hall}
        onChange={handleChange}
        placeholder="Hall"
        required
      />
      <button type="submit">{form.id ? 'Update' : 'Create'} Booking</button>
    </form>
  );
};

export default BookingForm;
